<? $is_own_rating = isset($this->user->user_id) AND	($this->user->user_id == $rating->manager_id OR	$this->user->user_id == $rating->client_id);?><div class='table bid' id='manager_rating<?=$rating->rating_id?>'>	<div class='angle angle-lt'></div>	<div class='angle angle-rt'></div>	<div class='angle angle-lb'></div>	<div class='angle angle-rb'></div>	<? View::show('main/elements/ratings/ratinginfo', array(		'rating' => $rating)); ?>	<br class="expander" style="clear:both;display:none;line-height:0;">	<div class='table admin-inside'		 style="margin-top:0px;padding-top:10px;padding-bottom:0;"		 id="comments<?= $rating->rating_id ?>">		<table>			<tr class="comment" <? if (empty($rating->comments)) : ?>style="display:none;"<? endif; ?>>				<th>					<b>Комментарии</b>				</th>			</tr>			<? if (isset($rating->comments)) : foreach ($rating->comments as $comment) : ?>				<tr class="comment">					<td>						<? View::show('main/elements/ratings/comment', array(							'comment' => $comment)); ?>					</td>				</tr>			<? endforeach; endif; ?>			<tr class='comment add-comment'				style="display:none;">				<td>					<? View::show('main/elements/ratings/newComment', array(						'rating' => $rating,						'is_own_rating' => $is_own_rating)); ?>				</td>			</tr>			<tr class='last-row'>				<td>					<div class='floatleft bid_buttons'						 style="width:100%;">						<div class='submit expand_comments'>							<div>								<input type='button'									   value='Развернуть переписку'									   onclick="expandComments('<?= $rating->rating_id ?>');">							</div>						</div>						<div class='submit collapse_comments' style='display:none;'>							<div>								<input type='button'									   value='Свернуть переписку'									   onclick="collapseComments('<?= $rating->rating_id ?>');">							</div>						</div>						<div class='floatleft'>							<img class="float comment_progress"								 style="display:none;margin:0px;margin-top:5px;"								 src="/static/images/lightbox-ico-loading.gif"/>						</div>					</div>				</td>			</tr>		</table>	</div></div><br><br>