<? $is_own_rating = isset($this->user->user_id) AND	($this->user->user_id == $rating->manager_id OR	$this->user->user_id == $rating->client_id);?><div class='table bid' id='manager_rating<?=$rating->rating_id?>'>	<div class='angle angle-lt'></div>	<div class='angle angle-rt'></div>	<div class='angle angle-lb'></div>	<div class='angle angle-rb'></div>	<? View::show('main/elements/ratings/ratinginfo', array(		'rating' => $rating)); ?>	<br class="expander" style="clear:both;display:none;line-height:0;">	<div class='table admin-inside' style="margin-top:0px;padding-top:10px;padding-bottom:0;" id="comments<?=$rating->rating_id?>">		<table>						<tr class="comment"<? if (empty($rating->comments)) : ?> style="display:none;"<? endif; ?>>				<th>					<b>Комментарии</b>				</th>			</tr>			<? if (isset($rating->comments)) : foreach ($rating->comments as $comment) : ?>			<tr class="comment">				<td>					<? View::show('main/elements/orders/comment', array(						'comment' => $comment)); ?>				</td>			</tr>			<? endforeach; endif; ?>			<tr class='last-row'>				<td>					<form class='admin-inside' action="<?= $selfurl ?>addBidComment/<?=$rating->rating_id?>" id="bidCommentForm" method="POST">						<div class='add-comment' style="display:none;">							<div><textarea id='comment' name='comment'></textarea></div>							<div class='submit comment-submit'><div><input type='submit' name="add" value="Добавить" /></div></div>						</div>					</form>				</td>			</tr>			<tr class='last-row'>				<td>					<div class='floatleft' style="width:100%;">							<div class='submit add_comment' <?= $is_own_rating ? '' :							'style="display:none;"'?>><div><input type='button' class="" value='Добавить комментарий'																  onclick="addComment('<?=$rating->rating_id?>');" /></div></div>						<div class='submit save_comment' style="display:none;"><div><input type='button' class="" value='Сохранить' onclick="saveComment('<?=$rating->rating_id?>');" /></div></div>						<div class='submit cancel_comment' style="display:none;"><div><input type='button' class="" value='Отмена' onclick="cancelComment('<?=$rating->rating_id?>');" /></div></div>						<div class='submit expand_comments' <? if (empty($rating->comments)) : ?>style='display:none;'<? endif; ?>><div><input type='button' value='Развернуть переписку' onclick="expandComments('<?=$rating->rating_id?>');" /></div></div>						<div class='submit collapse_comments' style='display:none;'><div><input type='button' value='Свернуть переписку' onclick="collapseComments('<?=$rating->rating_id?>');" /></div></div>						<? if (isset($this->user->user_group) AND							$this->user->user_group == 'manager' AND							$this->user->user_id == $rating->manager_id) : ?>						<div class='submit delete_bid'><div><input type='button' class="" value='Удалить отзыв'																   onclick="deleteItem('<?=$rating->rating_id?>');" /></div></div>						<? endif; ?>						<div class='floatleft'>							<img class="float comment_progress" style="display:none;margin:0px;margin-top:5px;" src="/static/images/lightbox-ico-loading.gif"/>						</div>					</div>				</td>			</tr>		</table>	</div></div><br><br><? if ($is_own_rating) : ?><script>	$(function() {		$('#bidCommentForm').ajaxForm({			target: $('#bidCommentForm').attr('action'),			type: 'POST',			dataType: 'html',			iframe: true,			beforeSubmit: function(formData, jqForm, options)			{				beforeSubmitComment();			},			success: function(response)			{				successSubmitComment(response);			},			error: function(response)			{				errorSubmitComment(response);			}		});		<?= editor('comment', 90, 920, 'PackageComment') ?>	});</script><? endif; ?>