<div class='managerinfo admin-inside'>	<img src='/static/images/avatar_big.png'>	<h2 style='margin-bottom: 10px;'><?= $manager->statistics->fullname ?> (№ <?= $manager->manager_user ?>)</h2>	<b style='color:orange;font-size:20px;margin-bottom: 20px;height: 10px;'>		<? if ($manager->is_cashback) : ?>		100% CASHBACK (Лимит на заказы: <?= $manager->cashback_limit ?> <?= $manager->statistics->currency ?>)		<? endif; ?>	</b>	<ul class='tabs'>		<li class='active profile'><div><a class='profile' href='javascript:void();'>Профиль</a></div></li>		<li class='client_ratings'><div><a class='client_ratings' href='javascript:void();'>Отзывы клиентов			</a></div></li>		<li class='pricelist'><div><a class='pricelist' href='javascript:void();'>Тарифы</a></div></li>		<li class='payments'><div><a class='payments' href='javascript:void();'>Способы оплаты</a></div></li>		<li class='delivery'><div><a class='delivery' href='javascript:void();'>Доставка</a></div></li>		<li class='mail_forwarding'><div><a class='mail_forwarding' href='javascript:void();'>Mail Forwarding</a></div></li>	</ul></div><script>	$(function() {		$('ul.tabs a')			.click(function(e) {				e.preventDefault();				$('ul.tabs li').removeClass('active');				$('div.dealer_tab').hide();								$('div.' + $(e.target).attr('class')).show();								$(this).parent().parent().addClass('active');			});	});</script>