<div id="editBidForm<?= $bid->bid_id ?>" style='display:none;'><? View::show('main/elements/orders/editBid'); ?></div><script type="text/javascript">	$(function() {		// калькулятор		$('#editBidForm'+edit_bid_id+' .order_delivery_cost')				.keypress(function(event) {					validate_float(event);				})				.change(function() {					order_delivery_cost = $(this).val();					if (isNaN(order_delivery_cost) || order_delivery_cost == '')					{						order_delivery_cost = 0;					}					refreshEditTotals();				});		// интерактив		$('.edit-bid').ajaxForm({			dataType: 'html',			iframe: true,			beforeSubmit: function()			{				$('img.bid_progress').show();			},			success: function(response)			{				$('img.bid_progress').hide();				if (response)				{					success('top', 'Предложение успешно сохранено.');					$('#editBidForm' + edit_bid_id).hide('slow');					var bid = $('div#bid' + edit_bid_id);					bid						.find('a.edit_button')						.attr('href', 'javascript:editBid(' + edit_bid_id + ');')						.html('изменить');					bid						.find('div.bid_buttons')						.show('slow');					bid						.find('div#comments' + edit_bid_id)						.show('slow');				}				else				{					error('top', 'Предложение не сохранено. Обновите страницу и попробуйте еще раз.');				}			},			error: function(response)			{				error('top', 'Предложение не сохранено. Обновите страницу и попробуйте еще раз.');				$('img.bid_progress').hide();			}		});		// новая комиссия		$('#editBidForm'+edit_bid_id+' input.manager_tax').change(function() {			manager_tax = parseGenericTax(this);			refreshEditTotals();		});		// новая комиссия в процентах		$('#editBidForm'+edit_bid_id+' input.manager_tax_percentage').change(function() {			manager_tax_percentage = parseGenericTax(this);			manager_tax = Math.ceil(manager_tax_percentage * order_products_cost * 0.01);			refreshEditTotals();		});		// новая комиссия за фото		$('#editBidForm'+edit_bid_id+' input.manager_foto_tax').change(function() {			manager_foto_tax = parseGenericTax(this);			refreshEditTotals();		});			});	var editBidFormInitialized = false;	function updateBid(bid_id)	{		$('form#editBid'+bid_id).submit();	}	/*function editFotoTax()	{		$('#editBidForm .foto_tax_plaintext').hide('fast');		$('#editBidForm .foto_tax_editor').show('fast');	}*/	/*function editManagerTax()	{		$('#editBidForm .manager_tax_plaintext').hide('fast');		$('#editBidForm .manager_tax_editor').show('fast');	}*/	/*function addExtraTax()	{		var $template = $('#editBidForm .template').clone();		$template			.removeClass('template')			.find('input.extra_tax_value')			.attr('name', 'extra_tax_value' + extra_tax_counter)			.keypress(function(event) {				validate_float(event);			})			.change(function() {				updateExtraTax();			});		$template			.find('input.extra_tax_name')			.attr('name', 'extra_tax_name' + extra_tax_counter)			.change(function() {				updateExtraTax();			});		$('#editBidForm div.extra_tax_box:last').after($template);		$template.show('fast');		extra_tax_counter++;		refreshEditTotals();	}*/	// доп. комиссии	/*function updateExtraTax()	{		extra_tax = 0;		extra_tax_hint = '';		$('#editBidForm input.extra_tax_value').each(function(index, item) {			extra_tax += parseGenericTax(item);		});		$('#editBidForm input.extra_tax_name').each(function(index, item) {			extra_tax_hint += (extra_tax_hint == '' ? '' : ', ');			extra_tax_hint += $(item).val();		});		refreshEditTotals();}*/	/*function removeExtraTax(image)	{		$(image)			.parent()			.parent()			.remove();		updateExtraTax();		refreshEditTotals();	}*/</script>