<div id='bid0' style="display:none;">	<h3>Новое предложение:</h3>	<form class='admin-inside' action="/manager/addBid/<?= $order->order_id ?>" id="bidForm" method="POST">		<input type="hidden" name="extra_tax_counter" class="extra_tax_counter" value="0">		<div class='table'>			<div class='angle angle-lt'></div>			<div class='angle angle-rt'></div>			<div class='angle angle-lb'></div>			<div class='angle angle-rb'></div>			<? if (empty($new_bid))			{				View::show('main/elements/orders/newbidinfo');			}			else			{				View::show('main/elements/orders/managerinfo', array('bid' => $new_bid));			}			?>			<h3>Расходы по заказу:</h3>			<div class="new_bid_box">				<div>					<span class="bold">						Общая стоимость товаров и местной доставки:					</span>					<span>						<b class="order_products_cost"></b>						<?= $order->order_currency ?>					</span>				</div>				<div>					<span>						Комиссия посредника:					</span>					<span style="display: none" class="manager_tax_editor">						<input type="text int"							   name="manager_tax"							   class="textbox manager_tax"							   maxlength='11'>						<b class="currency">							<?= $order->order_currency ?>						</b>						или						<input type="text int"							   name="manager_tax_percentage"							   class="textbox manager_tax_percentage"							   maxlength='3'							   style="width: 40px;">						<b class="percent">							%&nbsp;&nbsp;от общей стоимости товаров и местной доставки						</b>					</span>					<span class="manager_tax_plaintext">						<b class="manager_tax_percentage"></b>%						(<b class="manager_tax"></b> <?= $order->order_currency ?>)						<a href="javascript:editManagerTax();">изменить</a>					</span>				</div>				<div>					<span>						Фото товаров (<b class="requested_foto_count"></b> шт.):					</span>					<span style="display: none" class="foto_tax_editor">						<input type="text int"							   name="foto_tax"							   class="textbox manager_foto_tax"							   maxlength='11'>						<b class="currency">							<?= $order->order_currency ?>						</b>					</span>					<span class="foto_tax_plaintext">						<b class="manager_foto_tax"></b>						<?= $order->order_currency ?>						<a href="javascript:editFotoTax();">изменить</a>					</span>				</div>			</div>			<? if ($order->order_country_to) : ?>			<div class="new_bid_box">				<div>					<span class="bold">						Доставка <b class="order_weight"></b>г в:						<?= $order->order_city_to ?>						(<?= $order->order_country_to ?>)					</span>				</div>				<div>					<span>						Стоимость международной доставки*:					</span>					<span>						<input type="text"							   name="delivery_cost"							   class="textbox int"							   id="delivery_total"							   maxlength='11'>						<b class="currency">							<?= $order->order_currency ?>						</b>					</span>				</div>				<div>					<span>						Способ доставки*:					</span>					<span>						<input type="text"							   class="textbox"							   name="delivery_name"							   class="order_delivery_name"							   value="<?= $order->preferred_delivery ?>">					</span>				</div>			</div>			<? endif; ?>			<div class="new_bid_box">				<div>					<span class="bold">						<b>Дополнительные расходы по заказу:</b>					</span>					<span>						<b class="extra_tax">0</b>						<?= $order->order_currency ?>					</span>				</div>				<div class="extra_tax_box template" style="display: none;">					<span>						<input type="text"							   class="textbox extra_tax_name"							   name="extra_tax_name"							   maxlength="255"							   style="width: 253px;">					</span>					<span>						<input type="text"							   class="textbox extra_tax_value int"							   maxlength="11"							   name="extra_tax_value">						<b class="currency">							<?= $order->order_currency ?>						</b>						<img src="<?= IMG_PATH ?>delete.png"							 style="cursor: pointer; vertical-align: middle;"							 title="удалить"							 onclick="removeExtraTax(this);">					</span>				</div>				<div>					<span>						<img src="<?= IMG_PATH ?>plus.png"							 style="cursor: pointer; vertical-align: middle;"							 onclick="addExtraTax();">						<a href="javascript:addExtraTax();">Добавить</a>					</span>				</div>			</div>			<div class="new_bid_box">				<div>					<span class="bold">						<b>Комментарий для клиента:</b>					</span>				</div>				<div style="height: 40px;">					<span>						<textarea name="comment" class="textbox"></textarea>					</span>				</div>			</div>		</div>		<div style="height: 50px;" class="admin-inside">			<div class="submit">				<div>					<input type="button" value="Добавить" onclick="addBid();">				</div>			</div>			<div class="submit">				<div>					<input type="button" value="Отменить" onclick="cancelBid();">				</div>			</div>			<div class='floatleft'>				<img class="float bid_progress" style="display:none;margin:0px;margin-top:5px;"					 src="/static/images/lightbox-ico-loading.gif"/>			</div>		</div>	</form></div><script type="text/javascript">var manager_tax = <?= isset($order->manager_tax) ? $order->manager_tax : 0 ?>;var manager_tax_percentage = <?= isset($order->manager_tax_percentage) ? $order->manager_tax_percentage : 0 ?>;var manager_foto_tax = <?= $order->foto_tax ?>;var manager_foto_tax_percentage = <?= isset($order->manager_foto_tax_percentage) ?	$order->manager_foto_tax_percentage : 0 ?>;var requested_foto_count = <?= isset($order->requested_foto_count) ? $order->requested_foto_count : 0 ?>;var order_total_cost = <?= isset($order->order_total_cost) ? $order->order_total_cost : 0 ?>;var order_products_cost = <?= $order->order_products_cost + $order->order_delivery_cost ?>;var order_delivery_cost = 0;var order_delivery_name = '';var order_weight = <?= $order->order_weight ?>;var order_currency = '<?= $order->order_currency ?>';var extra_tax = 0;var extra_tax_counter = 0;$(function() {	// калькулятор	$('#delivery_total')		.keypress(function(event) {			validate_float(event);		})		.change(function() {			order_delivery_cost = $(this).val();			if (isNaN(order_delivery_cost) || order_delivery_cost == '')			{				order_delivery_cost = 0;			}			refreshTotals();		});	// интерактив	$('#bidForm').ajaxForm({		dataType: 'html',		iframe: true,		beforeSubmit: function()		{			$('img.bid_progress').show();		},		success: function(response)		{			$('img.bid_progress').hide();			if (response)			{				success('top', 'Предложение успешно отправлено клиенту! Посмотреть <a href="<?= BASEURL ?>">другие заказы</a>');				$('#newBidForm').hide('slow');				$('.bid_button').hide();				$('h3.bids_header').after(response).show();			}			else			{				error('top', 'Предложение не отправлено. Заполните все поля и сохраните его еще раз.');			}		},		error: function(response)		{			error('top', 'Предложение не отправлено. Заполните все поля и сохраните его еще раз.');			$('img.bid_progress').hide();		}	});	// новая комиссия	$('#bidForm input.manager_tax').change(function() {		manager_tax = parseGenericTax(this);		refreshTotals();	});	// новая комиссия в проентах	$('#bidForm input.manager_tax_percentage').change(function() {		manager_tax_percentage = parseGenericTax(this);		manager_tax = Math.ceil(manager_tax_percentage * order_products_cost * 0.01);		refreshTotals();	});	// новая комиссия за фото	$('#bidForm input.manager_foto_tax').change(function() {		manager_foto_tax = parseGenericTax(this);		refreshTotals();	});});function login_handler(){	$.ajax({		url: '<?= BASEURL ?>manager/getNewBid/<?= $order->order_id ?>/<?= rand(0, 1000)?>',		dataType: 'html',		success: function(data) {			eval(data);		}	});}</script>