<div id='bid0' style="display:none;">	<h3>Новое предложение:</h3>	<form class='admin-inside' action="/manager/addBid" id="bidForm" method="POST">		<input type="hidden" name="order_id" value="<?= $order->order_id ?>">		<input type="hidden" name="manager_tax" class="manager_tax">		<input type="hidden" name="order_total_cost" class="order_total_cost">		<input type="hidden" name="manager_foto_tax" class="manager_foto_tax">		<input type="hidden" name="order_products_cost" class="order_products_cost">		<input type="hidden" name="order_delivery_cost" class="order_delivery_cost">		<div class='table'>			<div class='angle angle-lt'></div>			<div class='angle angle-rt'></div>			<div class='angle angle-lb'></div>			<div class='angle angle-rb'></div>			<? View::show('main/elements/orders/managerinfo', array('bid' => $new_bid)); ?>			<h3>Расходы по заказу:</h3>			<span class="label">				<b>Общая стоимость товаров в заказе:</b>			</span>			<span class="label">				<b class="order_products_cost"></b>				<?= $order->order_currency ?>			</span>			<br>			<span class="label">				Комиссия посредника:			</span>			<span class="label">				<b class="manager_tax_percentage"></b>%				(<b class="manager_tax"></b> <?= $order->order_currency ?>)			</span>			<a href="javascript:void(0);">изменить</a>			<br>			<span class="label">				Фото товаров (<b class="requested_foto_count"></b> шт.):			</span>			<span class="label">				 <b class="manager_foto_tax"></b>				<?= $order->order_currency ?>			</span>			<a href="javascript:void(0);">изменить</a>			<br>			<br>			<span class="label">				Доставка <b class="order_weight"></b> г в:				<?= $order->order_city_to ?>				(<?= $order->order_country_to ?>)			</span>			<br>			<span class="label">				Стоимость доставки*:				<input type="text" id="delivery_total" style='margin-left:6px;' maxlength='10'>				<?= $order->order_currency ?>			</span>			<br>			<span class="label">				Способ доставки*:				<input type="text" name="delivery_name" class="order_delivery_name" style='margin-left:30px;'					   value="<?= $order->preferred_delivery ?>">			</span>			<br>			<br>			<span class="label">				<b>Дополнительные расходы по заказу:</b>				<b class="additional_tax"></b>				<?= $order->order_currency ?>			</span>			<br>			<br>			<span class="label">				<b>Комментарий для клиента:</b>			</span>			<br>			<span class="label">				<textarea name="comment" style="resize: vertical;"></textarea>			</span>		</div>		<div style="height: 50px;" class="admin-inside">			<div class="submit">				<div>					<input type="button" value="Добавить" onclick="addBid();">				</div>			</div>			<div class="submit">				<div>					<input type="button" value="Отменить" onclick="cancelBid();">				</div>			</div>			<div class='floatleft'>				<img class="float bid_progress" style="display:none;margin:0px;margin-top:5px;"					 src="/static/images/lightbox-ico-loading.gif"/>			</div>		</div>	</form></div><script type="text/javascript">var manager_tax = <?= $order->manager_tax ?>;var manager_tax_percentage = <?= $order->manager_tax_percentage ?>;var manager_foto_tax = <?= $order->foto_tax ?>;var manager_foto_tax_percentage = <?= $order->manager_foto_tax_percentage ?>;var requested_foto_count = <?= $order->requested_foto_count ?>;var order_total_cost = <?= $order->order_total_cost ?>;var order_products_cost = <?= $order->order_products_cost + $order->order_delivery_cost ?>;var order_delivery_cost = 0;var order_delivery_name = '';var order_weight = <?= $order->order_weight ?>;var order_currency = '<?= $order->order_currency ?>';$(function() {	// калькулятор	$('#delivery_total')		.keypress(function(event) {			validate_float(event);		})		.change(function() {			order_delivery_cost = $(this).val();			if (isNaN(order_delivery_cost) || order_delivery_cost == '')			{				order_delivery_cost = 0;			}			refreshTotals();		});	// интерактив	$('#bidForm').ajaxForm({		dataType: 'html',		iframe: true,		beforeSubmit: function()		{			$('img.bid_progress').show();		},		success: function(response)		{			$('img.bid_progress').hide();			if (response)			{				success('top', 'Предложение успешно отправлено клиенту! Посмотреть <a href="<?= BASEURL ?>">другие заказы</a>');				$('#newBidForm').hide('slow');				$('.bid_button').hide();				$('h3.bids_header').after(response).show();			}			else			{				error('top', 'Предложение не отправлено. Заполните все поля и сохраните его еще раз.');			}		},		error: function(response)		{			error('top', 'Предложение не отправлено. Заполните все поля и сохраните его еще раз.');			$('img.bid_progress').hide();		}	});});</script>