<div id="newBidForm" style='display:none;'>	<? if (empty($this->user->user_group)) : ?>	<? View::show('main/elements/auth/newBid'); ?>	<? endif; ?>	<? View::show('main/elements/orders/newBid'); ?></div><script type="text/javascript">	function showNewBidForm(id)	{		$('div#newBidForm').show('slow');		$('div#newBidButton').hide('slow');	}	function cancelBid()	{		$('div#newBidForm').hide('slow');		$('div#newBidButton').show('slow');	}		function refreshTotals()	{		order_total_cost = 			order_products_cost +			manager_tax +			foto_tax +			parseFloat(order_delivery_cost);					order_total_cost = Math.ceil(order_total_cost * 100) / 100;		$('#order_total_cost').val(order_total_cost);		$('#order_products_cost').val(order_products_cost);		$('#manager_tax').val(manager_tax);		$('#foto_tax').val(foto_tax);		$('#order_delivery_cost').val(order_delivery_cost);				$('div#newBidForm span.order_total_cost').html(order_total_cost);	}		$(function() {		// калькулятор		$('#delivery_total')			.keypress(function(event) {				validate_float(event);			})			.change(function() {				order_delivery_cost = $(this).val();				if (isNaN(order_delivery_cost) || order_delivery_cost == '')				{					order_delivery_cost = 0;				}								refreshTotals();			});				// интерактив		$('#bidForm').ajaxForm({			target: $('#bidForm').attr('action'),			type: 'POST',			dataType: 'html',			iframe: true,			beforeSubmit: function(formData, jqForm, options)			{								$('img.bid_progress').show();			},			success: function(response)			{				$('img.bid_progress').hide();								if (response)				{					success('top', 'Предложение успешно отправлено клиенту! Посмотреть <a href="/">другие заказы</a>.');					$('#newBidForm').hide('slow');					$('.bid_button').hide();					$('h3.bids_header').after(response).show();								}				else				{					error('top', 'Предложение не отправлено. Заполните все поля и сохраните его еще раз.');				}			},			error: function(response)			{				error('top', 'Предложение не отправлено. Заполните все поля и сохраните его еще раз.');				$('img.bid_progress').hide();			}		});	});		function addBid()	{		$('#bidForm').submit();	}</script>