<div id="newBidForm" style='display:none;'><? if (empty($this->user->user_group)){	View::show('main/elements/auth/newBid');}else{	View::show('main/elements/orders/newBid');} ?></div><script type="text/javascript">	var bidFormInitialized = false;	function initBidForm()	{		bidFormInitialized = true;		refreshTotals();	}	function showNewBidForm()	{		$('div#bid0').each(function() {			if ( ! bidFormInitialized)			{				initBidForm();			}			$('div#bid0').show('slow');		});		$('div#newBidForm').show();		$('div#newBidButton').hide('slow');	}	function cancelBid()	{		$('div#bid0').hide('slow');		$('div#newBidButton').show('slow');	}		function refreshTotals()	{		order_total_cost = 			order_products_cost +			manager_tax +			manager_foto_tax +			parseFloat(order_delivery_cost);					order_total_cost = Math.ceil(order_total_cost * 100) / 100;		/*		$('#order_total_cost').val(order_total_cost);		$('#order_products_cost').val(order_products_cost);		$('#manager_tax').val(manager_tax);		$('#foto_tax').val(manager_foto_tax);		$('#order_delivery_cost').val(order_delivery_cost);		*/		$bid = $('#bid0');		$bid				.find('.manager_tax')				.val(manager_tax)				.html(manager_tax);		$bid				.find('.manager_tax_percentage')				.val(manager_tax_percentage)				.html(manager_tax_percentage);		$bid				.find('.manager_foto_tax')				.val(manager_foto_tax)				.html(manager_foto_tax);		$bid				.find('.requested_foto_count')				.val(requested_foto_count)				.html(requested_foto_count);		$bid				.find('.manager_foto_tax')				.val(manager_foto_tax)				.html(manager_foto_tax);		$bid				.find('.order_total_cost')				.val(order_total_cost)				.html(order_total_cost);		$bid				.find('.order_delivery_cost')				.val(order_delivery_cost)				.html(order_delivery_cost);		$bid				.find('.order_products_cost')				.val(order_products_cost)				.html(order_products_cost);		$bid				.find('.order_weight')				.val(order_weight)				.html(order_weight);		// прячем нулевые значения		/*		if (order_delivery_cost)		{			$bid.find('.delivery_total_box').show();		}		else		{			$bid.find('.delivery_total_box').hide();		}		if (manager_foto_tax)		{			$bid.find('.foto_total_box').show();		}		else		{			$bid.find('.foto_total_box').hide();		}*/	}		function addBid()	{		$('#bidForm').submit();	}</script>