<div id="newBidForm" style='display:none;'>	<h3>Новое предложение:</h3>	<form class='admin-inside' action="/manager/addBid" id="bidForm" method="POST">		<input type="hidden" name="order_id" id="order_id" value="<?=$order->order_id?>" />		<input type="hidden" name="manager_tax" id="manager_tax" value="<?=$order->manager_tax?>" />		<input type="hidden" name="order_total_cost" id="order_total_cost" value="<?=$order->order_total_cost?>" />		<input type="hidden" name="manager_foto_tax" id="manager_foto_tax" value="<?=$order->manager_foto_tax?>" />		<input type="hidden" name="order_products_cost" id="order_products_cost" value="<?=$order->order_products_cost?>" />		<input type="hidden" name="order_delivery_cost" id="order_delivery_cost" value="<?=$order->order_delivery_cost?>" />		<div class='table'>			<div class='angle angle-lt'></div>			<div class='angle angle-rt'></div>			<div class='angle angle-lb'></div>			<div class='angle angle-rb'></div>			<? View::show('main/elements/orders/managerinfo', array('bid' => $new_bid)); ?>			<h3>Расходы по заказу:</h3>			<span class="label">				<b>Общая стоимость товаров в заказе:</b>			</span>			<span class="label">				<?= $order->order_products_cost ?> <?=$order->order_currency?>			</span>			<br>			<span class="label">				Комиссия посредника:			</span>			<span class="label">				<b>10%</b> (<?= $order->manager_tax ?> <?=$order->order_currency?>)			</span>			<a href="javascript:void(0);">изменить</a>			<br>			<span class="label">				Фото товаров: (<?=$order->requested_foto_count?> шт.)			</span>			<span class="label">				 <b><?=$order->foto_tax?> <?=$order->order_currency?></b>			</span>			<a href="javascript:void(0);">изменить</a>			<br>			<span class="label">				<b>Доставка 1234567<?//=$order->order_product_weight?>г в:					<?=$order->order_city_to?> (<?=$order->order_country_to?>)</b>			</span>			<br>			<span class="label">Стоимость доставки*:<input type="text" id="delivery_total" value="" style='margin-left:6px;' maxlength='10'> <?=$order->order_currency?></span>			<br>			<span class="label">Способ доставки*:<input type="text" name="delivery_name" value=""														style='margin-left:30px;'></span>			<br>			<span class="label request_comment">				Комментарий:				<textarea name="comment" style="resize: vertical;"></textarea>				<br style='clear:both;'>			</span>			<? if (empty($this->user->user_group)) : ?>			<? View::show('main/elements/auth/new_order'); ?>			<? endif; ?>		</div>		<div style="height: 50px;" class="admin-inside">			<div class="submit">				<div>					<input type="button" value="Добавить" onclick="addBid();">				</div>			</div>			<div class="submit">				<div>					<input type="button" value="Отменить" onclick="cancelBid();">				</div>			</div>			<div class='floatleft'>				<img class="float bid_progress" style="display:none;margin:0px;margin-top:5px;"					 src="/static/images/lightbox-ico-loading.gif"/>			</div>		</div>	</form></div><script type="text/javascript">	var manager_tax = <?=$order->manager_tax?>;	var foto_tax = <?=$order->foto_tax?>;	var requested_foto_count = <?=$order->requested_foto_count?>;	var manager_foto_tax = <?=$order->manager_foto_tax?>;	var order_total_cost = <?=$order->order_total_cost?>;	var order_products_cost = <?=$order->order_products_cost?>;	var order_delivery_cost = <?=$order->order_delivery_cost?>;	function showNewBidForm(id)	{		$('div#newBidForm').toggle('slow');	}	function cancelBid()	{		$('div#newBidForm').hide('slow');	}		function refreshTotals()	{		order_total_cost = 			order_products_cost +			manager_tax +			foto_tax +			parseFloat(order_delivery_cost);					order_total_cost = Math.ceil(order_total_cost * 100) / 100;		$('#order_total_cost').val(order_total_cost);		$('#order_products_cost').val(order_products_cost);		$('#manager_tax').val(manager_tax);		$('#foto_tax').val(foto_tax);		$('#order_delivery_cost').val(order_delivery_cost);				$('div#newBidForm span.order_total_cost').html(order_total_cost);	}		$(function() {		// калькулятор		$('#delivery_total')			.keypress(function(event) {				validate_float(event);			})			.change(function() {				order_delivery_cost = $(this).val();				if (isNaN(order_delivery_cost) || order_delivery_cost == '')				{					order_delivery_cost = 0;				}								refreshTotals();			});				// интерактив		$('#bidForm').ajaxForm({			target: $('#bidForm').attr('action'),			type: 'POST',			dataType: 'html',			iframe: true,			beforeSubmit: function(formData, jqForm, options)			{								$('img.bid_progress').show();			},			success: function(response)			{				$('img.bid_progress').hide();								if (response)				{					success('top', 'Предложение успешно отправлено клиенту! Посмотреть <a href="/">другие заказы</a>.');					$('#newBidForm').hide('slow');					$('.bid_button').hide();					$('h3.bids_header').after(response).show();								}				else				{					error('top', 'Предложение не отправлено. Заполните все поля и сохраните его еще раз.');				}			},			error: function(response)			{				error('top', 'Предложение не отправлено. Заполните все поля и сохраните его еще раз.');				$('img.bid_progress').hide();			}		});	});		function addBid()	{		$('#bidForm').submit();	}</script>