<div id="newBidForm" style='display:none;'><? View::show('main/elements/orders/newBid'); ?></div><script type="text/javascript">	var bidFormInitialized = false;	$(function() {		// калькулятор		$('#delivery_total')			.keypress(function(event) {				validate_float(event);			})			.change(function() {				order_delivery_cost = $(this).val();				if (isNaN(order_delivery_cost) || order_delivery_cost == '')				{					order_delivery_cost = 0;				}				refreshTotals();			});		// интерактив		$('#bidForm').ajaxForm({			dataType: 'html',			iframe: true,			beforeSubmit: function()			{				$('img.bid_progress').show();			},			success: function(response)			{				$('img.bid_progress').hide();				if (response)				{					success('top', 'Предложение успешно отправлено клиенту! Посмотреть <a href="<?= $this->config->item('base_url') ?>">другие заказы</a>');					$('#newBidForm').hide('slow');					$('.bid_button').hide();					$('h3.bids_header').after(response).show();				}				else				{					error('top', 'Предложение не отправлено. Заполните все поля и сохраните его еще раз.');				}			},			error: function(response)			{				error('top', 'Предложение не отправлено. Заполните все поля и сохраните его еще раз.');				$('img.bid_progress').hide();			}		});		// новая комиссия		$('#bidForm input.manager_tax').change(function() {			manager_tax = parseGenericTax(this);			refreshTotals();		});		// новая комиссия в процентах		$('#bidForm input.manager_tax_percentage').change(function() {			manager_tax_percentage = parseGenericTax(this);			manager_tax = Math.ceil(manager_tax_percentage * order_products_cost * 0.01);			refreshTotals();		});		// новая комиссия за фото		$('#bidForm input.manager_foto_tax').change(function() {			manager_foto_tax = parseGenericTax(this);			refreshTotals();		});	});</script>