<form class='comments' action='<?= $selfurl ?>addO2iComment/<?= $o2i->order2in_id ?>' method='POST'>	<? if ( ! $comments): ?>	<div class='comment'>		Пока нет комментариев<br/>	</div>	<? else: ?>	<?  foreach ($comments as $comment): ?>		<div class='comment'>			<div class='question'>						<span class="name">№<?= $comment->o2icomment_id ?>							<? if ($comment->o2icomment_user == $o2i->order2in_user): ?>								Вы:								<? else: ?>								Администратор:								<? endif; ?>							<br /><?= formatCommentDate($comment->o2icomment_time) ?>						</span>				<p><?= $comment->o2icomment_text ?></p>			</div>		</div>		<?  endforeach; ?>	<? endif; ?>	<? if ($user): ?>	<div class='add-comment'>		<div class='textarea'><textarea name='comment'></textarea></div>		<div class='submit'><div><input type='submit' name="add" value="Добавить" /></div></div>	</div>	<? endif; ?></form>