<div class='table'>	<div class='angle angle-lt'></div>	<div class='angle angle-rt'></div>	<div class='angle angle-lb'></div>	<div class='angle angle-rb'></div>	<table>		<tr>			<th>				Статус:			</th>			<th nowrap>				<? if ($this->user->user_group == 'client') : ?>				<b>					<?= $statuses[$o2i->order2in_status] ?>				</b>				<? else : ?>				<form id="paymentForm" action="<?= $selfurl ?>updatePayment/<?= $o2i->order2in_id ?>" method="POST">					<select id="payment_status" name="payment_status">						<? foreach ($statuses as $status => $status_name) : ?>						<option value="<?= $status ?>" <? if ($o2i->order2in_status == $status) :							?>selected<? endif; ?>><?= $status_name ?></option>						<? endforeach; ?>					</select>					<img class="float" id="paymentProgress" style="display:none;vertical-align: middle;margin-left: 5px;"						 src="/static/images/lightbox-ico-loading.gif"/>					<? endif; ?>				</form>			</th>		</tr>		<tr>			<td>				Оплата заказа			</td>			<td>				<a href="/<?= $this->user->user_group ?>/order/<?= $o2i->order_id ?>">№<?= $o2i->order_id ?></a>				<?= $order_types[$order->order_type] ?>			</td>		</tr>		<tr>			<td>				Сумма оплаты			</td>			<td>				<?= $o2i->order2in_amount ?>				<?= $order->order_currency ?>			</td>		</tr>		<? if ($o2i->is_countrypost) : ?>		<tr>			<td>				Сумма перевода			</td>			<td>				<?= $o2i->order2in_amount_local ?>				<?= $o2i->order2in_currency ?>			</td>		</tr>		<? endif; ?>		<tr>			<td>				Способ оплаты			</td>			<td>				<? View::show('/main/elements/payments/payment_description',					array('payment' => $o2i)); ?>			</td>		</tr>		<tr>			<td>				Отправитель			</td>			<td>				<a href="/<?= $client->statistics->login ?>"><?= $client->statistics->fullname ?></a>				(<?= $client->statistics->login ?>)			</td>		</tr>		<tr>			<td>				Получатель			</td>			<td>				<a href="/<?= $manager->statistics->login ?>"><?= $manager->statistics->fullname ?></a>				(<?= $manager->statistics->login ?>)			</td>		</tr>		<tr>			<td>				Дата добавления			</td>			<td>				<?= date("d.m.Y H:i", strtotime($o2i->order2in_createtime)) ?>			</td>		</tr>	</table></div><? if ($this->user->user_group != 'client') : ?><script>$(function() {	$('select#payment_status').change(function() {		$('#paymentForm').submit();	});	$('#paymentForm').ajaxForm({		dataType: 'html',		iframe: true,		beforeSubmit: function(formData, jqForm, options)		{			$("#paymentProgress").show();		},		success: function(response)		{			if (response == '')			{				$("#paymentProgress").hide();				success('top', 'Заявка №<?= $o2i->order2in_id ?> успешно сохранена!');			}			else			{				$("#paymentProgress").hide();				error('top', 'Заявка №<?= $o2i->order2in_id ?> не сохранена. Попробуйте еще раз.');			}		},		error: function(response)		{			$("#paymentProgress").hide();			error('top', 'Заявка №<?= $o2i->order2in_id ?> не сохранена. Попробуйте еще раз.');		}	});});</script><? endif; ?>