<?//if (isset($this->user->user_id)) echo "ok";//if ($this->user->user_id != -1) echo "ok";if ($this->user->user_id != $rating->manager_id) {    if ( $this->user->user_group == "manager" ) return;    if ( $rating->client_id != $this->user->user_id ) return;}?><div class="managerinfo">	<form class='admin-inside'		  action="profile/addRatingComment/<?= $rating->rating_id ?>"		  id="ratingCommentForm<?= $rating->rating_id ?>" method="POST">		<input type="hidden" class="rating_id" value="<?= $rating->rating_id ?>">        <img src="/main/avatar/<?= $this->user->user_id ?>" width="56px" height="56px">		<span class='label floatleft'			  style="padding-top: 4px;">			<img src="/static/images/comment-reply.png"				 style="margin-right:8px;margin-left:-4px;margin-top: -3px;">			<img src="/static/images/flags/<?= $this->session->userdata['user_country_name_en'] ?>.png"				 style="margin-right:10px;margin-top: -4px;padding-top: 0;top: -2px;">			<a href="<?= $this->config->item('base_url') . $this->user->user_login ?>"><?= $this->session->userdata['user_name'] ?></a>			(<?= $this->user->user_login ?>)			&nbsp;&nbsp;&nbsp;			<?= date('d.m.Y H:i') ?>		</span>		<br style="height: 1px;line-height: 1px;">		<div style="float: left;margin-top: 0;width: 835px;">			<span class='label'>			<textarea id='comment<?= $rating->rating_id ?>'					  name='comment'					style="margin-top: 6px;margin-bottom: 5px;width: 550px;height: 50px;resize: vertical;border: 1px					solid #D7D7D7;					"></textarea>			</span>			<div class='submit save_comment floatleft'>				<div style="margin-top: 0;">				<div style="margin-top: 0;">					<input type='button' class="" value='Добавить' onclick="saveCommentRating('<?= $rating->rating_id ?>',this);" />				</div>				</div>			</div>		</div>	</form></div><? if ($is_own_rating) : ?><script>	$(function() {		$('#ratingCommentForm<?= $rating->rating_id ?>').ajaxForm({			dataType: 'html',			iframe: true,			beforeSubmit: function(formData, jqForm, options)			{                var mes = $("#comment<?= $rating->rating_id ?>").attr('value');                re = /\s/ig;                mes1 =  mes.replace(re,'')                if (mes1.length < 5)                {                    error('top', 'Заполните все поля и сохраните еще раз.');                    return false;                }                var $comments = $('div#comments' + <?= $rating->rating_id ?>);                $comments                    .find('img.comment_progress<?= $rating->rating_id ?>')                    .show('slow');			},			success: function(response)			{				//successSubmitComment(response, <?= $rating->rating_id ?>);                success('top', 'Комментарий добавлен!');                var $comments = $('div#comments' + <?= $rating->rating_id ?>);                $comments                    .find('img.comment_progress<?= $rating->rating_id ?>')                    .hide('slow');                $("tr.insert-comment<?= $rating->rating_id ?>").before('<tr class="comment hide"><td>' + response + '</td></tr>');                $("#comment<?= $rating->rating_id ?>").attr('value','');			},			error: function(response)			{				errorSubmitComment(response, <?= $rating->rating_id ?>);			}		});	});</script><? endif; ?>