<h3 class="bids_header" <?= $ratings ? '' : 'style="display:none;"' ?> >Все	отзывы</h3><? if ($ratings){	foreach ($ratings as $rating)	{		View::show('main/elements/dealers/manager_rating', array(				'rating' => $rating));	}} ?><script>	function expandBidDetails(bid_id)	{		$('div#bid' + bid_id)			.find('.biddetails div')			.css('display', 'block');		$('div#bid' + bid_id)			.find('.expander')			.css('display', 'block');		$('div#bid' + bid_id)			.find('.biddetails div.expand')			.hide();			}	function collapseBidDetails(bid_id)	{		$('div#bid' + bid_id)			.find('.biddetails div')			.hide();		$('div#bid' + bid_id)			.find('.expander')			.hide();		$('div#bid' + bid_id)			.find('.biddetails div.expand')			.css('display', 'block');			}	function addComment(bid_id)	{		$('#comments' + bid_id)			.find('.add_comment,.expand_comments,.collapse_comments,.delete_bid')			.hide('slow');					$('#comments' + bid_id)			.find('.save_comment,.cancel_comment,.add-comment')			.show('slow');	}		function cancelComment(bid_id)	{		$('#comments' + bid_id).find('.add_comment,.delete_bid').show('slow');		if (true == eval('window.comment' + bid_id + 'expanded'))		{			$('#comments' + bid_id).find('.collapse_comments').show('slow');		}		else		{			$('#comments' + bid_id).find('.expand_comments').show('slow');		}				$('#comments' + bid_id).find('.save_comment,.cancel_comment,.add-comment').hide('slow');	}		function saveComment(bid_id)	{		$('#bidCommentForm').submit();	}		$(function() {		$('#bidCommentForm').ajaxForm({			target: $('#bidCommentForm').attr('action'),			type: 'POST',			dataType: 'html',			iframe: true,			beforeSubmit: function(formData, jqForm, options)			{								beforeSubmitComment();			},			success: function(response)			{				successSubmitComment(response);			},			error: function(response)			{				errorSubmitComment(response);							}		});	});		function beforeSubmitComment()	{		$('img.comment_progress').show('slow');		$('.cancel_comment').hide('slow');	}		function successSubmitComment(response)	{		$('img.comment_progress').hide('slow');				if (response)		{			success('top', 'Комментарий добавлен!');			$('.add_comment,.delete_bid').show('slow');						var bid_id = $(response).find('input').val();						$('.save_comment,.collapse_comments,.expand_comments,.cancel_comment,.add-comment').hide('slow');						$('#comments' + bid_id).find('tr.comment:last').after('<tr class="comment"><td>' + response + '</td></tr>');			$('#comments' + bid_id).find('tr.comment').show('slow');						$('#comments' + bid_id).find('.collapse_comments').show('slow');			$('#comments' + bid_id).find('.expand_comments').hide('slow');			eval('window.comment' + bid_id + 'expanded = true;');			//$('.add-comment').html(''); TODO: cleanup the textarea		}		else		{			error('top', 'Комментарий не добавлен. Напишите что-нибудь и сохраните его еще раз.');			$('.cancel_comment').show('slow');		}	}	function errorSubmitComment(response)	{		$('img.comment_progress').hide('slow');		$('.cancel_comment').show('slow');		error('top', 'Комментарий не добавлен. Напишите что-нибудь и сохраните его еще раз.');	}		function expandComments(bid_id)	{		$('div#comments' + bid_id + ' tr.comment').show('slow');		$('div.expand_comments').hide('slow');		$('div.collapse_comments').show('slow');				eval('window.comment' + bid_id + 'expanded = true;');	}		function collapseComments(bid_id)	{		$('div#comments' + bid_id + ' tr.comment').hide('slow');		$('div.expand_comments').show('slow');		$('div.collapse_comments').hide('slow');				eval('window.comment' + bid_id + 'expanded = false;');	}		function chooseBid(bid_id)	{		$('#chooseBidForm' + bid_id).submit();	}		function beforeSubmitChoose(bid_id)	{		$('#chooseBidForm' + bid_id)			.parent()			.find('img.comment_progress')			.show('slow');		$('#chooseBidForm' + bid_id)			.find('.choose_bid')			.hide('slow');	}		function successSubmitChoose(response, bid_id)	{		$('#chooseBidForm' + bid_id)			.parent()			.find('img.comment_progress')			.hide('slow');				if (response)		{			success('top', 'Посредник выбран!');			$('h3.bids_header').html('Выбран посредник');			$('#chooseBidForm' + bid_id)				.find('.choose_bid')				.hide('slow');			$('div.clientOrderInfo')				.after(response);			$('div.clientOrderInfo:first')				.remove();			$('h3.clientOrderInfo')				.show('slow');		}		else		{			error('top', response);			$('#chooseBidForm' + bid_id)				.parent()				.find('img.comment_progress')				.hide('slow');			$('#chooseBidForm' + bid_id)				.find('.choose_bid')				.show('slow');					}	}	function errorSubmitChoose(response, bid_id)	{		error('top', 'Посредник не выбран. Попробуйте еще раз.');		$('#chooseBidForm' + bid_id)			.parent()			.find('img.comment_progress')			.hide('slow');		$('#chooseBidForm' + bid_id)			.find('.choose_bid')			.show('slow');	}</script>