<div class='ratinginfo'>	<? View::show('/main/elements/ratings/rating_block', array('rating' => $rating)); ?>	<img src="/main/avatar/<?= $rating->client_id ?>" width="56px" height="56px">	<a href="/<?= $rating->statistics->login ?>"><?= $rating->statistics->client_name ?></a>	(<?= $rating->statistics->login ?>)	&nbsp;    <? if ($rating->rating_type) : ?>        <img src="<?= IMG_PATH ?><?= $rating->rating_type > 0 ? 'positive' : 'negative' ?>.png"             style="margin: 0px 0 0 5px; float: none;top: 1px;position: relative;"             title="Это <?= $rating->rating_type > 0 ? 'положительный' : 'отрицательный' ?> отзыв">    <? endif; ?>	<div class="created">		<div class='label'>			<?= isset($rating->created) ? date('d.m.Y H:i', strtotime($rating->created)) : date('d.m.Y H:i') ?>		</div>    </div>	<br>	<br style="clear: both;height: 0;line-height: 0;"></div>