<div class='ratinginfo'>	<? View::show('/main/elements/ratings/rating_block', array('rating' => $rating)); ?>	<img src="/main/avatar/<?= $rating->client_id ?>" width="56px" height="56px">	<a href="/<?= $rating->statistics->login ?>"><?= $rating->statistics->fullname ?></a>	(<?= $rating->statistics->login ?>)	&nbsp;	<div class="created">		<span class='label'>			<?= isset($rating->created) ? date('d.m.Y H:i', strtotime($rating->created)) : date('d.m.Y H:i') ?>		</span>	</div>	<? if ($rating->rating_type) : ?>	<img src="<?= IMG_PATH ?><?= $rating->rating_type > 0 ? 'positive' : 'negative' ?>.png"		 style="margin: 5px 0 0 5px; float: none;top: 3px;position: relative;"		 title="Это <?= $rating->rating_type > 0 ? 'положительный' : 'отрицательный' ?> отзыв">	<? endif; ?>	<br>	<br style="clear: both;height: 0;line-height: 0;"></div>