<div class='ratinginfo'>	<img src='<?= IMG_PATH ?>avatar.png'>	<a href="/<?= $rating->statistics->login ?>"><?= $rating->statistics->fullname ?></a>	(<?= $rating->statistics->login ?>)	&nbsp;&nbsp;&nbsp;	<? View::show('/main/elements/ratings/reviews', array(		'positive' =>  $rating->statistics->positive_reviews,		'neutral' =>  $rating->statistics->neutral_reviews,		'negative' =>  $rating->statistics->negative_reviews,	)); ?>	<? if ($rating->rating_type) : ?>	<img src="<?= IMG_PATH ?><?= $rating->rating_type > 0 ? 'positive' : 'negative' ?>.png"		 style="margin: 2px 0 0 0; float: right;"		 title="Это <?= $rating->rating_type > 0 ? 'положительный' : 'отрицательный' ?> отзыв">	<? endif; ?>	<? View::show('/main/elements/ratings/rating_block', array(		'rating' => $rating	)); ?>	<br>	<div class="created">		<span class='label'			  style="margin-right: 10px;">			<?= isset($rating->created) ? date('d.m.Y H:i', strtotime($rating->created)) : date('d.m.Y H:i') ?>		</span>		<!--span class='label'>			Выполненных заказов:			<?= 12345//$rating->statistics->completed_orders ?>		</span-->	</div>	<br style="clear: both;height: 0;line-height: 0;"></div>