<div class='managerinfo admin-inside'>	<h2>Мой профиль</h2>	<ul class='tabs'>		<style>			.top-block {				min-height: auto;			}		</style>		<li class='active profile'><div><a class='profile'>Персональные данные</a></div></li>		<li class='blog'><div><a class='blog'>Новости</a></div></li>		<li class='pricelist'><div><a class='pricelist'>Тарифы</a></div></li>		<li class='payments'><div><a class='payments'>Оплата</a></div></li>		<li class='delivery'><div><a class='delivery'>Доставка</a></div></li>	</ul></div><script>	$(function() {		$('ul.tabs a')			.click(function(e) {				e.preventDefault();				$('ul.tabs li').removeClass('active');				$('div.dealer_tab').hide();								$('div.' + $(e.target).attr('class')).show();								$(this).parent().parent().addClass('active');			});	});</script>